import React, { useEffect, useState, useMemo } from 'react';
import { Link } from 'react-router-dom';
import socketio from 'socket.io-client';
import api from '../../services/api';

import './styles.css';

export default function Dashboard() {
    const [spots, setSpots] = useState([]);
    const [requests, setRequests] = useState([]);

    const userid = localStorage.getItem('user');
    const socket = useMemo(() => socketio('http://localhost:3333', {
        query: { userid }
    }), [userid]);

    useEffect(() => {
        socket.on('booking_request', data => {
            setRequests([ ...requests, data])
        })
    }, [requests, socket]);

    useEffect(() => {
        async function loadSpots() {
            const userid = localStorage.getItem('user');
            const response = await api.get('/dashboard', {
                headers: {userid}
            });

            setSpots(response.data)
        }

        loadSpots();
    }, []);

    async function handleAccept(id) {
        await api.post(`/bookings/${id}/approvals`);

        setRequests(requests.filter(request => request._id != id));
    };

    async function handleReject(id) {
        await api.post(`/bookings/${id}/rejections`);

        setRequests(requests.filter(request => request._id != id));
    };

    return (
        <>
            <ul className="notifications">
                {requests.map(request => (
                    <li key={request._id}>
                        <p>
                            <strong>{request.user.email}</strong> est√° solicitando uma reserva em <strong>{request.spot.empresa}</strong> para a data: <strong>{request.date}</strong>
                        </p>
                        <button className="accept" onClick={() => handleAccept(request._id)}>ACEITAR</button>
                        <button className="reject" onClick={() => handleReject(request._id)}>REJEITAR</button>
                    </li>
                ))}
            </ul>
            <ul className="spot-list">
                {spots.map( spot => (
                    <li key={spot._id}>
                        <header style={{ backgroundImage: `url(${spot.imagem_url})` }} />
                        <strong>{spot.empresa}</strong>
                        <span>{spot.valor ? `R$${spot.valor}/dia` : 'GRATUITO'}</span>
                    </li>
                ))}
            </ul>

            <Link to="/new" >
                <button className="btn">Cadastrar novo spot</button>
            </Link>
        </>
    )
}
